      }  
      //Generate progress meter events if supported  
      if ((site.EventInterest & WordBoundaryFlag) != 0) {  
      string[] subs = newFrags[i].TextToSpeak.Split(spaces);  
  
      foreach (string s in subs) {  
        int offset = newFrags[i].TextOffset;  
        SpeechEventInfo spEvent = new SpeechEventInfo((Int16)TtsEventId.WordBoundary,   
                (Int16)EventParameterType.Undefined,   
                 s.Length, new IntPtr(offset));  
        offset += s.Length;  
        if (s.Trim().Length > 0) {  
          SpeechEventInfo[] events = new SpeechEventInfo[1];  
          events[0] = spEvent;  
          site.AddEvents(events, 1);  
        }  
      }  
      }  
    }  
  }  
  
  _baseSynthesize.Speak(newFrags, wfx, site);  
  
}  
```

  
  
## Remarks  
 <span data-ttu-id="c4f40-143">La valeur par défaut de cette propriété est `System.String.Empty`.</span><span class="sxs-lookup"><span data-stu-id="c4f40-143">The default value of this property is `System.String.Empty`.</span></span>  
  
 <span data-ttu-id="c4f40-144">Les applications sont libres de modifier complètement la valeur de <xref:System.Speech.Synthesis.TtsEngine.TextFragment.TextToSpeak%2A>.</span><span class="sxs-lookup"><span data-stu-id="c4f40-144">Applications are free to completely change the value of <xref:System.Speech.Synthesis.TtsEngine.TextFragment.TextToSpeak%2A>.</span></span>  
  
 <span data-ttu-id="c4f40-145">Redéfinition de la valeur de <xref:System.Speech.Synthesis.TtsEngine.TextFragment.TextToSpeak%2A> ne changera pas la valeur de <xref:System.Speech.Synthesis.TtsEngine.TextFragment.TextOffset%2A> et <xref:System.Speech.Synthesis.TtsEngine.TextFragment.TextLength%2A>.</span><span class="sxs-lookup"><span data-stu-id="c4f40-145">Resetting the value of <xref:System.Speech.Synthesis.TtsEngine.TextFragment.TextToSpeak%2A> will not change the value of <xref:System.Speech.Synthesis.TtsEngine.TextFragment.TextOffset%2A> and <xref:System.Speech.Synthesis.TtsEngine.TextFragment.TextLength%2A>.</span></span>  
  
   
  
